// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity 0.8.4;

import "../interface/IBridge.sol";
import "../interface/IHandler.sol";

interface IERC20 {
    function balanceOf(address account) external returns (uint256);

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) external;

    function transfer(address to, uint256 amount) external;
}

interface IERC20MintAble {
    function mint(address to, uint256 amount) external;
}

interface IERC20burnable {
    function burnFrom(address account, uint256 amount) external;
}

contract ERC20Handler is IHandler {
    // admin地址
    address private _admin;

    // bridge合约地址
    address public bridgeAddress;

    // 跨链的合约地址
    address public tokenAddress;

    // 跨链的合约是否需要burn
    bool public burnable;

    // resourceID => target hanger address  （目标链上）的ERC20handler地址
    mapping(bytes32 => address) public resourceIDToTargetERC20HandlerAddress;

    // token contract address => is burnable
    mapping(address => bool) public burnList;

    // Initializes Manager
    constructor(address _bridgeAddress, address _tokenAddress) {
        _admin = msg.sender;
        bridgeAddress = _bridgeAddress;
        tokenAddress = _tokenAddress;
    }

    // 设置resourceID （目标链上）的ERC20handler地址
    function adminSetResourceIDTargetERC20Handler(
        bytes32 resourceID,
        address handlerAddress
    ) public {
        require(_admin == msg.sender, "only called by admin");
        resourceIDToTargetERC20HandlerAddress[resourceID] = handlerAddress;
    }

    // 设置token地址
    function adminSetTokenAddress(address _tokenAddress) public {
        tokenAddress = _tokenAddress;
    }

    // 设置跨链代币是否需要销毁
    function adminSetBurnable(bool _burnable) public {
        burnable = _burnable;
    }

    // 跨链
    function deposit(
        bytes32 resourceID,
        uint32 targetChainId,
        uint256 amount
    ) public returns (bytes32) {
        address targetHandler = resourceIDToTargetERC20HandlerAddress[
            resourceID
        ];
        require(
            targetHandler != address(0),
            "resourceID not mapped to target erc20 handler address"
        );

        IERC20 erc20 = IERC20(tokenAddress);
        require(erc20.balanceOf(msg.sender) >= amount, "Insufficient funds");

        if (burnable) {
            // burn
            IERC20burnable erc20Burn = IERC20burnable(tokenAddress);
            erc20Burn.burnFrom(msg.sender, amount);
        } else {
            // lock
            erc20.transferFrom(msg.sender, address(this), amount);
        }

        bytes memory data = abi.encode(msg.sender, amount);
        // Call bridge
        IBridge bridge = IBridge(bridgeAddress);
        bytes32 messageId = bridge.callRemote(
            resourceID,
            targetChainId,
            targetHandler,
            data
        );

        return messageId;
    }

    // 目标链执行
    function execute(bytes32 resourceID, bytes calldata data) public override {
        address handlerAddress = resourceIDToTargetERC20HandlerAddress[
            resourceID
        ];
        require(
            handlerAddress != address(this),
            "resourceID not mapped to this handler"
        );

        uint256 amount;
        address sender;
        (sender, amount) = abi.decode(data, (address, uint256));
        if (burnable) {
            // mint
            IERC20MintAble erc20mint = IERC20MintAble(tokenAddress);
            erc20mint.mint(sender, amount);
        } else {
            // release
            IERC20 erc20 = IERC20(tokenAddress);
            erc20.transfer(sender, amount);
        }
    }
}

