// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity 0.8.4;

import "../interface/IBridge.sol";
import "../interface/IERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

// 平台币->ERC20 转账
contract NativeToERC20Handler is Ownable {
    // resourceID
    bytes32 public rID;

    // bridge合约地址
    address public bridgeAddress;

    // 跨链的代币合约地址
    address public tokenAddress;

    // 目标链上ERC20 handler地址
    address public targetHandler;

    // 跨链的代币是否需要burn
    bool public burnable;

    event Received(address sender);

    // 此方法可以让合约接受平台币,必须要有
    receive() external payable {
        emit Received(msg.sender);
    }

    // Initializes Manager
    constructor(
        bytes32 _resourceID,
        address _bridgeAddress,
        address _tokenAddress,
        address _targetHandler,
        bool _burnable
    ) {
        rID = _resourceID;
        bridgeAddress = _bridgeAddress;
        tokenAddress = _tokenAddress;
        targetHandler = _targetHandler;
        burnable = _burnable;
    }

    // 设置
    function adminSetConfig(
        address _bridgeAddress,
        address _tokenAddress,
        address _targetHandler,
        bool _burnable
    ) public onlyOwner {
        bridgeAddress = _bridgeAddress;
        tokenAddress = _tokenAddress;
        targetHandler = _targetHandler;
        burnable = _burnable;
    }

    function deposit(bytes32 resourceID, uint32 targetChainId)
        public
        payable
        returns (bytes32)
    {
        require(resourceID == rID, "resourceID error");

        require(
            msg.value > 0,
            "condition: The transfer amount must be greater than 0 (msg.value > 0)"
        );

        uint256 amount = msg.value;
        require(msg.sender.balance >= amount, "Insufficient funds");

        if (burnable) {
            // burn
            Address.sendValue(payable(address(0)), amount);
        } else {
            payable(address(this)).transfer(msg.value); // lock
        }

        bytes memory data = abi.encode(msg.sender, amount);
        // Call bridge
        IBridge bridge = IBridge(bridgeAddress);
        bytes32 messageId = bridge.callRemote(
            resourceID,
            targetChainId,
            targetHandler,
            data
        );

        return messageId;
    }

    function execute(bytes32 resourceID, bytes calldata data) public {
        require(resourceID == rID, "resourceID error");
        uint256 amount;
        address sender;
        (sender, amount) = abi.decode(data, (address, uint256));
        if (burnable) {
            // mint
            IERC20Mintable erc20Mint = IERC20Mintable(tokenAddress);
            erc20Mint.mint(sender, amount);
        } else {
            // release
            IERC20 erc20 = IERC20(tokenAddress);
            erc20.transfer(sender, amount);
        }
    }

    // 用于测试
    function NativeBalanceOfSender() external view returns (uint256) {
        return msg.sender.balance;
    }

    // 用于测试
    function NativeBalanceOfThisHandler() external view returns (uint256) {
        return address(this).balance;
    }
}

